import React, { useState, useEffect, useReducer } from "react";

import Card from "../UI/Card/Card";
import classes from "./Login.module.css";
import Button from "../UI/Button/Button";

const emailReducer = (state, action) => {

  if (action.type === 'USER_INPUT') {
    return { value: action.val, isValid: action.val.includes("@") };
  }
  return { value: "", isValid: false };
};

const Login = (props) => {
  // const [enteredEmail, setEnteredEmail] = useState("");
  // const [emailIsValid, setEmailIsValid] = useState();
  const [enteredPassword, setEnteredPassword] = useState("");
  const [passwordIsValid, setPasswordIsValid] = useState();
  const [enteredClgName, setClgName] = useState("");
  const [clgNameIsvalid, setClgNameIsvalid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const [emailState, dispatchMail] = useReducer(emailReducer, {
    value: "",
    isValid: false,
  });

  // useEffect(() => {
  //   const identifier = setTimeout(() => {
  //     console.log('checing')
  //     setFormIsValid(
  //       enteredEmail.includes('@') && enteredPassword.trim().length > 6 && enteredClgName.trim().length > 8
  //     );
  //   },1000);

  //   return () => {
  //     console.log('clean')
  //     clearTimeout(identifier)
  //   }

  // }, [enteredEmail, enteredPassword, enteredClgName])

  const emailChangeHandler = (event) => {
      dispatchMail({type: 'USER_INPUT', val: event.target.value})

    setFormIsValid(
      event.target.value.includes("@") &&
        enteredPassword.trim().length > 6 &&
        enteredClgName.trim().length > 8
    );
  };

  const passwordChangeHandler = (event) => {
    setEnteredPassword(event.target.value);

    setFormIsValid(
      emailState.isValid.includes("@") &&
        event.target.value.trim().length > 6 &&
        enteredClgName.trim().length > 8
    );
  };

  const clgNameHandler = (event) => {
    setClgName(event.target.value);

    setFormIsValid(
      emailState.isValid.includes("@") &&
        enteredPassword.trim().length > 6 &&
        event.target.value.trim().length > 8
    );
  };

  const validateClgHandler = () => {
    setClgNameIsvalid(enteredClgName.trim().length > 8);
  };

  const validateEmailHandler = () => {
    setEmailIsValid(emailState.isValid);
  };

  const validatePasswordHandler = () => {
    setPasswordIsValid(enteredPassword.trim().length > 6);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.value, enteredPassword, enteredClgName);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordIsValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={enteredPassword}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            clgNameIsvalid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="clgName">College Name:</label>
          <input
            type="text"
            id="clgName"
            value={enteredClgName}
            onChange={clgNameHandler}
            onBlur={validateClgHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
