import React, {useState} from "react";
import './ExpenseForm.css';

const ExpenseForm = () => {
    const [enteredTitle, titleChangeHandler] = useState('')

    const titleHandler = (event) => {
        titleChangeHandler(event.target.value)
    }
    return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type='text' onChange={titleHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type='number' min="0.0" step="0.50"></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type='date' min="2020-01-01" max="2024-12-31"></input>
                </div>
                <div className="new-expense__actions">
                    <button type="submit">Add Expense</button>
                </div>

            </div>
        </form>
    )
}

export default ExpenseForm;
