import React, { useContext } from "react";
import Modal from "../UI/Modal";
import CartItem from "./CartItem";
import classes from "./Cart.module.css";
import CartContext from "../../store/CartContext";

const Cart = (props) => {
  const cartCntxt = useContext(CartContext);
  
const totalAmount = `$${cartCntxt.totalAmount.toFixed(2)}`;
 const hasItems = cartCntxt.items.length > 0;

const cartItemRemove = id => {};
const cartItemAdd = item => {
  cartCntxt.addItem({...item, amount:'1'})
};

  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCntxt.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onAdd={cartItemAdd.bind(null, item.id)}
          onRemove={cartItemRemove.bind(null,item)}
        />
      ))}
    </ul>
  );

  return (
    <Modal onClose={props.onClose}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onClose}>
          Close
        </button>
        {hasItems && <button className={classes.button}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;
